### /workspace/frontend/src/App.js
```javascript
1: import React from 'react';
2: import ToDoList from './ToDoList';
3: import ToDoForm from './ToDoForm';
4: 
5: // Main entry point for the React frontend
6: const App = () => {
7:   const [items, setItems] = React.useState([]);
8:   const [error, setError] = React.useState(null);
9: 
10:   // Fetch existing to-dos on mount
11:   React.useEffect(() => {
12:     fetch('/api/todos')
13:       .then(res => {
14:         if (!res.ok) throw new Error('Failed to fetch to-dos');
15:         return res.json();
16:       })
17:       .then(data => setItems(data))
18:       .catch(err => setError(err.message));
19:   }, []);
20: 
21:   // Add a new to-do
22:   const addTodo = async (todo) => {
23:     const res = await fetch('/api/todos', {
24:       method: 'POST',
25:       headers: { 'Content-Type': 'application/json' },
26:       body: JSON.stringify(todo),
27:     });
28:     if (!res.ok) {
29:       const err = await res.text();
30:       throw new Error(err || 'Failed to add todo');
31:     }
32:     const created = await res.json();
33:     setItems(prev => [...prev, created]);
34:   };
35: 
36:   // Update a to-do
37:   const updateTodo = async (id, updated) => {
38:     const res = await fetch(`/api/todos/${id}`, {
39:       method: 'PUT',
40:       headers: { 'Content-Type': 'application/json' },
41:       body: JSON.stringify(updated),
42:     });
43:     if (!res.ok) {
44:       const err = await res.text();
45:       throw new Error(err || 'Failed to update todo');
46:     }
47:     const updatedItem = await res.json();
48:     setItems(prev => prev.map(it => it.id === id ? updatedItem : it));
49:   };
50: 
51:   // Delete a to-do
52:   const deleteTodo = async (id) => {
53:     const res = await fetch(`/api/todos/${id}`, { method: 'DELETE' });
54:     if (!res.ok) {
55:       const err = await res.text();
56:       throw new Error(err || 'Failed to delete todo');
57:     }
58:     setItems(prev => prev.filter(it => it.id !== id));
59:   };
60: 
61:   // Handlers to pass down
62:   const handleToggleComplete = (id, updated) => updateTodo(id, updated);
63:   const handleDelete = (id) => deleteTodo(id);
64: 
65:   return (
66:     <div style={styles.app}>
67:       <h1 style={styles.header}>To-Do Manager</h1>
68:       {error && (
69:         <div style={styles.error}>{error}</div>
70:       )}
71:       <ToDoForm onAdd={addTodo} />
72:       <ToDoList items={items} onToggleComplete={handleToggleComplete} onDelete={handleDelete} onError={setError} />
73:     </div>
74:   );
75: };
76: 
77: const styles = {
78:   app: {
79:     fontFamily: 'Arial, sans-serif',
80:     padding: '20px',
81:   },
82:   header: {
83:     marginBottom: '16px',
84:   },
85:   error: {
86:     color: 'red',
87:     marginBottom: '8px',
88:   },
89: };
90: 
91: export default App;
```
